// Inicialización de AOS (Animate On Scroll)
import AOS from 'aos';
import 'aos/dist/aos.css';

// Configuración global de AOS
document.addEventListener('DOMContentLoaded', () => {
  AOS.init({
    duration: 800, // Duración de la animación en milisegundos
    easing: 'ease-in-out', // Tipo de aceleración
    once: true, // La animación solo se ejecuta una vez
    mirror: false, // Si las animaciones se repiten al hacer scroll hacia arriba
    offset: 100 // Desplazamiento (en px) desde el borde inferior de la ventana
  });
});

export default AOS;